[Form]
public class AAXPurchLineClose extends FormRun
{
    Date                   fromDate            = dateNull();
    Date                   toDate              = prevMth(prevMth(prevMth(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))));

    PurchLineDeliveryType  orderTypeListPage   = PurchLineDeliveryType::Receipts;

    InventDimCtrl_Frm_ActiveRightClick          inventDimFormSetup;

}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParm;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init          :
            InventDimSetupGrid::initInventDimParmInventTrans(inventDimParm);

            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_ActiveRightClick::newFromForm(element);
            }
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm);
            inventDimFormSetup.formSetControls(true);
            break;
    }
}

/// <summary>
///
/// </summary>
private void findOrCreateDateAndOrderTypeRanges(QueryBuildDataSource _qbds)
{
    const int expectedDateFieldNum        = fieldNum(AAXPurchLineOpen, ExpectedDate);
    const int remainPurchPhysicalFieldNum = fieldNum(AAXPurchLineOpen, RemainPurchPhysical);

    QueryBuildRange qbrExpectedDate = _qbds.findRange(expectedDateFieldNum);
    QueryBuildRange qbrOrderType = _qbds.findRange(remainPurchPhysicalFieldNum);

    if (!qbrExpectedDate || !qbrOrderType)
    {
        _qbds.clearRanges();
        qbrExpectedDate = _qbds.addRange(expectedDateFieldNum);
        qbrOrderType = _qbds.addRange(remainPurchPhysicalFieldNum);
    }

    qbrExpectedDate.value(queryRange(fromDate, toDate));
    qbrExpectedDate.status(RangeStatus::Hidden);

    qbrOrderType.value('!0');
    qbrOrderType.status(RangeStatus::Hidden);
}

int  selectionChange()
{
    orderTypeListPage = this.selection();
    AAXPurchLineOpen_ds.research();
    return super();
}

public boolean modified()
{
    fromDate = this.dateValue();
    AAXPurchLineOpen_ds.research();
    return super();
}

